@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

html {
  /* Theme */
  --color-accent: #716996;
  --color-shade: #e2e1ea;

  --font-heading: "Fredoka", sans-serif;
  --font-base: "Lato", sans-serif;

  /* Default property values */
  --background: #f4f3f0;
  --color: #333;
  --padding: 2rem;
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 8px;
  --max-width: 1250px;
  --border-color: #cbcace;
  --transition-time: 0.1s;
  --transition: var(--transition-time) color,
    var(--transition-time) background-color, var(--transition-time) border-color,
    var(--transition-time) text-decoration-color,
    var(--transition-time) box-shadow, var(--transtion-time) transform;
}

#page {
  font-family: var(--font-base);
  color: var(--color);
  line-height: 1.6;
  font-size: 1.25rem;
  font-weight: 300;
  background: var(--background);
}

.content {
  max-width: 800px;
  margin: 0 auto;
  padding: 4rem 2rem;

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  & > p {
    line-height: 1.5;
    margin-bottom: 1.5rem;
  }

  a {
  }

