  }

  blockquote {
    padding: 2rem;
    margin-bottom: 1.5rem;
    border-left: 5px solid var(--color-accent);
  }
}

.section-container {
  width: 100%;
  max-width: var(--max-width, 1200px);
  margin: 0 auto;
  padding: 5rem 2rem;
}

