    font-size: 2.75rem;
  }
}

.button {
  color: #f6f6f9;
  font-size: 1rem;
  font-weight: 500;
  border-radius: 2rem;
  padding: 10px 24px;

  background: var(--color-accent);
  transition: var(--transition);
  white-space: nowrap;

  &:hover {
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent);
    border: 2px solid var(--color-accent);
  }
}

@keyframes pulse {
  from {
    transform: scale(0.75);
  }

  to {
    transform: scale(1);
  }
}

/* Screen-readers only */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

